function Table=error_map(pol_deg)    % Add all subfolders to working directory    add_fem_lab();        % Initialize necessary constants    MAX_MESH_SIZE_COUNTER = 7;        % Compute vector 'Error' containing the L2 error    % of our approximation at a given mesh size    for mesh_size_counter=1:MAX_MESH_SIZE_COUNTER        % Solve FEM problem        [error_L2,runtime] = main(1/(2^mesh_size_counter),pol_deg) ;        % Assign error from the finite element problem solving to the Error vector.        Error(mesh_size_counter) = error_L2 ;        % save current mesh size        mesh_size(mesh_size_counter)=1/(2^mesh_size_counter);        % save current polynomial degree        degree(mesh_size_counter) = pol_deg;    endfor        Table=[degree',mesh_size',Error'];    endfunction