function error_map()    % Add all subfolders to working directory    addpath(genpath([pwd '/functions']))        min_mesh_size = 5;    max_pol_deg = 2;        error_map = zeros(min_mesh_size, max_pol_deg);    for mesh_size=1:min_mesh_size        for pol_deg = 1:max_pol_deg            [error_L2,runtime] = main( 1/mesh_size , pol_deg) ;            error_map(mesh_size,pol_deg) = error_L2 ;         endfor    endfor    % Plot error map    for mesh_size = 1:min_mesh_size        x(mesh_size) = 1/ mesh_size;    endfor    pol_deg = 1:max_pol_deg;    figure(4);    mesh(x,pol_deg ,error_map);    %axis([1 max_pol_deg 0 error_map(1,1) ]);    xlabel ("mesh size");    ylabel ("polynomial degree");    zlabel ("error");endfunction