function error_map()    % Add all subfolders to working directory    add_fem_lab();        % Initialize necessary constants    MAX_MESH_SIZE_COUNTER = 10;    MAX_POL_DEG = 3;        error_map = zeros(MAX_MESH_SIZE_COUNTER, MAX_POL_DEG);    for pol_deg = 1:MAX_POL_DEG        for mesh_size_counter=1:MAX_MESH_SIZE_COUNTER            [error_L2,runtime] = main(1/(2*mesh_size_counter),pol_deg) ;            error_map(mesh_size_counter,pol_deg) = error_L2 ;         endfor    endfor    % Plot error map    for mesh_size_counter = 1:MAX_MESH_SIZE_COUNTER        x(mesh_size_counter) = 1/ (mesh_size_counter*2);    endfor    pol_deg = 1:MAX_POL_DEG;    figure(4);    x    pol_deg    error_map    surf(x,pol_deg ,error_map');    set(gca,'zscale','log')    %axis([1 MAX_POL_DEG 0 error_map(1,1) ]);    xlabel ("mesh size");    ylabel ("polynomial degree");    zlabel ("error");endfunction