function val = hf_eval_poly(x,y,coefficient_vector)    j=1;     % order of basis    level=0; % For the level look at the ordering of the basisfunctions    val=0;    while j < length(coefficient_vector)        base_x=level;        base_y=0;        while(base_y < level)            val= val + coefficient_vector(j)*x.^(base_x)*y.^(base_y);            base_y++;            j++;        endwhile                base_x = 0;        base_y = level;        while(base_x < level)            val= val+ coefficient_vector(j)*x.^(base_x)*y.^(base_y);            base_x++;            j++;        endwhile                val= val+ coefficient_vector(j)*x.^(level)*y.^(level);        j++;        level++;    endwhileendfunction