function index = hf_exponent_to_column(x_exponent,y_exponent)    max_exponent = 30;    data_set = zeros(max_exponent,3);    j=1;     % order of basis    level=0; % For the level look at the ordering of the basisfunctions    while j<30        base_x=level;        base_y=0;        while(base_y < level)            data_set(j,:) = [base_x , base_y, j];            base_y++;            j++;        endwhile                base_x = 0;        base_y = level;        while(base_x < level)            data_set(j,:) = [base_x , base_y, j];            base_x++;            j++;        endwhile           data_set(j,:)=[level,level,j];        level++;        j++;    endwhile        %Check where x_exponent is in data_set    for i=1:length(data_set)        if(x_exponent == data_set(i,1))            if(y_exponent == data_set(i,2))                break;            endif        endif    endfor        index = data_set(i,3);endfunction