% Goal is to build a system A, which we can solve to obtain the coefficients of our polynomial f(x,y).% data_set should consist in first row of x values, 2nd row y values and 3rd row z values. f(x,y) = z.function coefficient_vector = hf_interpolation(data_set)    A = zeros(length(data_set));    j=1;     % row in matrix A    level=0; % For the level look at the ordering of the Basisfunctions    while j <= length(data_set)        base_x=level;        base_y=0;        while(base_y < level)            for i=1:length(data_set)                A(i,j)=data_set(i,1)^(base_x)*data_set(i,2)^(base_y);            endfor            base_y++;            j++;        endwhile                base_x = 0;        base_y = level;        while(base_x < level)            for i=1:length(data_set)                A(i,j)=data_set(i,1)^(base_x)*data_set(i,2)^(base_y);            endfor            base_x++;            j++;        endwhile                for i=1:length(data_set)            A(i,j)=data_set(i,1)^(level)*data_set(i,2)^(level);        endfor        j++;        level++;    endwhile            rhs = zeros(length(data_set),1);    for i=1:length(data_set)        rhs(i,1) = data_set(i,3);    endfor    % Solve linear system.    coefficient_vector = A \ rhs;    coefficient_vector = transpose(coefficient_vector);endfunction