% Goal is to build a system A, which we can solve to obtain the coefficients of our interpolated npolynomial f(x,y).% data_set should consist in first row of x values, 2nd row y values and 3rd row z values such that f(x,y) = z.function coefficient_vector = hf_interpolation(data_set)    A = zeros(length(data_set));    j=1;     % column in matrix A    level=0;     % create matrix A    while j <= length(data_set)        exponent_x=level;        exponent_y=0;        while(exponent_y < level)            for i=1:length(data_set)                A(i,j)=data_set(i,1)^(exponent_x)*data_set(i,2)^(exponent_y);            endfor            exponent_y++;            j++;        endwhile        exponent_x = 0;        exponent_y = level;        while(exponent_x < level)            for i=1:length(data_set)                A(i,j)=data_set(i,1)^(exponent_x)*data_set(i,2)^(exponent_y);            endfor            exponent_x++;            j++;        endwhile        for i=1:length(data_set)            A(i,j)=data_set(i,1)^(level)*data_set(i,2)^(level);        endfor        j++;        level++;    endwhile    %right hand side of the system consists of the 3rd column of data_set    rhs = zeros(length(data_set),1);          for i=1:length(data_set)        rhs(i,1) = data_set(i,3);    endfor    % Solve linear system.    coefficient_vector = A \ rhs;    coefficient_vector = transpose(coefficient_vector);endfunction