function value = int_gauss(x_1,w_1,x_2,w_2,f)     % x are the sample points and w are the weights.    % f is a function.    % Calculate number of sample points.    dim_1 = length(x_1);    dim_2 = length(x_2)        % Initialize starting value    value = 0;        % Calculcate 2 dimensional gauss    for i=1:dim_1        for j=1:dim_2            value = value + f(x_1(i),x_2(j))*w_2(j)*w_1(i);        endfor    endfor    endfunction