function value = int_gauss(x,w,f)     % x are the sample points and w are the weights.    % f is a function.    % Calculate number of sample points.    dim = length(x);        % Initialize starting value    value = 0;        % Calculcate 2 dimensional gauss    for i=1:dim        for j=1:dim            value = value + f(x(i),x(j))*w(j)*w(i);        endfor    endfor    endfunction