function node_number_local = mesh_appropriate_renumbering(mesh_size, polynomial_deg, cell_number, node_number_global)    % mesh properties    mesh_cells_per_row=(1/mesh_size);    mesh_nodes_per_row=(polynomial_deg*mesh_cells_per_row)+1;    % calculate node number and cell properties    dimension_domain = 2;    k = 0;    cell_nodes_counter =  (factorial(dimension_domain)/(factorial(k)*factorial(dimension_domain-k))) * polynomial_deg^k * (polynomial_deg+1)^(dimension_domain-k)    cell_row_counter = polynomial_deg + 1 ;    cell_nodes_per_row = cell_nodes_counter / cell_row_counter;    % calculate number of first node of our cell    first_node=1;    cell_number_iterated = 1;    mesh_column_counter = cell_row_counter*mesh_cells_per_row - mesh_cells_per_row;    cell_column=1;    counter=0;    step_counter = 0;            while cell_number_iterated<10           %{         while counter < cell_nodes_counter            for i=counter:cell_nodes_per_row-1+counter            first_node+i                if(first_node+i == node_number_global)                    break;                endif            endfor            counter=counter+10;        endwhile        %}                cell_number_iterated++;        %{        for k=1:mesh_column_counter            if(first_node+cell_nodes_per_row-1 == mesh_nodes_per_row*k)                first_node = 1+mesh_nodes_per_row*(cell_row_counter-1);                break;            elseif                first_node = first_node + cell_nodes_per_row - 1;                break;             endif        endfor         %}            endwhile       %{    Check if node_number_global is even in cell we wanted    if(cell_number_iterated != cell_number)        node_number_local = 0;        Return local number of the node in the cell    elseif        node_number_local=step_counter+1;       % while node_number_local <= cell_nodes_counter       %     if(first_node == node_number_global)        %        break;       %     endif       %             %    first_node = first_node        %endwhile    endif    %}        endfunction