% mesh_generate divides the unit square into equivilant squares with diameter mesh_sizefunction [vertex,cell] = mesh_generate(mesh_size)    side_length = mesh_size;            % side length of the element square    vertex_nr = 1;                 % initialise matrix saving all vertices. 1st column represents number of vertex, 2nd column represents x value and 3rd column y value.    % fill vertex matrix with its values orderd in lexicographical order    for y_val=0:side_length:1        for x_val=0:side_length:1            vertex(vertex_nr,1:2) = [x_val,y_val];            vertex_nr++;        endfor    endfor            % fill cell matrix with its values in lexicographical order    % vertex_i defines the number of the vertex in the vertex matrix    vertex_3 = 0;    vertex_1 = 1;    while vertex_3<=vertex_nr        vertex_2 = vertex_1+1;        vertex_3 = vertex_1+(1/side_length);        vertex_4 = vertex_3+1;        cell(vertex_1,1:4) = [vertex_1,vertex_2,vertex_3,vertex_4];        vertex_1++;    endwhile            endfunction