function node_number_local = mesh_renumber(cell_matrix, cell_number, node_number_global)    % Check if node_number_global belongs to cell with number cell_number    location = 0;    for i=1:length(cell_matrix)        if(cell_matrix(cell_number,i) == node_number_global)            location = i;            break        endif    endfor    node_number_local = location;endfunction