function m_plot_solution(solution_vector,polynomial_deg,mesh_size)    % We want to plot our approximated solution and the analytical solution.    tic    % ---- Mesh and shape function properties -----    SF = sf_generate(polynomial_deg);    [vertex,cell] = mesh_generate(mesh_size);    % ---------------------------------------------        % Analytical solution    f = @(x,y) (1/(1+2*pi^2)).*cos(pi.*x).*cos(pi.*y);        % Right hand side of the initial problem    g = @(x,y) cos(pi.*x).*cos(pi.*y);        % Plotting properties    x=0:1/50:1;    y=x;        % Initialize matrices saving the value of the function for specific x and y values.    Z = zeros(length(x),length(y));    A = zeros(length(x),length(y));        % Calculate matrix saving the value of the approximated function for values x(i) and y(j)    for i=1:length(x)        for j=1:length(y)            Z(i,j)=hf_eval_solution_vectorized(x(i),y(j),solution_vector,cell,vertex,polynomial_deg,SF);        endfor    endfor        % Calculate matrices saving the value of the for analytical solution and right hand side of initial problem for values x(i) and y(j)    [X,Y]=meshgrid(x,x);        % Plot analytical solution    figure(1);    mesh(x,y,f(X,Y));    axis([0 1 0 1 f(1,0) f(0,0)]);    xlabel ("x");    ylabel ("y");    zlabel ("u(x,y)");        % Plot right hand side of initial problem    figure(2);    mesh(x,y,g(X,Y));    axis([0 1 0 1 -1 1]);    xlabel ("x");    ylabel ("y");    zlabel ("f(x,y)");        % Plot approximated solution by FEM    figure(3);    mesh(x,y,Z);    axis([0 1 0 1 Z(1,length(x)) Z(1,1)]);    xlabel ("x");    ylabel ("y");    zlabel ("u_h(x,y)");    plot=tocendfunction