function cell_matrix_node = node_cell(mesh_size,polynomial_deg)dimension_domain = 2;k = 0;% nodes_counter gives us the number of nodes we except for a specific polynomial degreenodes_per_cell =  (factorial(dimension_domain)/(factorial(k)*factorial(dimension_domain-k))) * polynomial_deg^k * (polynomial_deg+1)^(dimension_domain-k)cell_counter = (1/mesh_size)^2cell_matrix_node = zeros(cell_counter, nodes_per_cell);row_counter = polynomial_deg + 1 nodes_per_row = nodes_per_cell / row_counter;node_counter = 1;node_per_cell_iteration=0;k=1;while node_per_cell_iteration <= nodes_per_cell    for j=1:cell_counter        for i=k:nodes_per_row            cell_matrix_node(j,i) = node_counter;            node_counter++;        endfor        node_counter--;endfornode_per_cell_iteration= node_per_cell_iteration + nodes_per_row;k=k+nodes_per_rownodes_per_row= 2*nodes_per_row;node_counter = endwhileendfunction