% create a matrix saving in the first column the x value of a node and in the 2nd column the y value of the node% nodes are in lexicographical orderfunction node = node_generate(polynomial_deg,mesh_size)    x=0;    y=0;    i=1;    while y <= 1 &&  x <= 1        while x<=1             node(i,:)=[x,y];            x=x+mesh_size/polynomial_deg;            i++;        endwhile        y=y+mesh_size/polynomial_deg;        x=0;    endwhileendfunction