function RHS = rhs_integrate(SF,vertex,cell)    %value of integration    val=0;    first_shape_function = SF(1,:)    %first shape function belongs to 1th node    node_belongs_to_cells = node_to_cells(1,vertex,cell);    %cellwise integration?    if(node_belongs_to_cells(4)!=0)        [vertex_1_x,vertex_1_y] = vertex(cell(node_belongs_to_cells(4),1),:)        [vertex_2_x,vertex_2_y] = vertex(cell(node_belongs_to_cells(4),2),:)        [vertex_3_x,vertex_3_y] = vertex(cell(node_belongs_to_cells(4),3),:)        [vertex_4_x,vertex_4_y] = vertex(cell(node_belongs_to_cells(4),4),:)            endif        if(node_belongs_to_cells(3)!=0)        [vertex_1_x,vertex_1_y] = vertex(cell(node_belongs_to_cells(3),1),:)        [vertex_2_x,vertex_2_y] = vertex(cell(node_belongs_to_cells(3),2),:)        [vertex_3_x,vertex_3_y] = vertex(cell(node_belongs_to_cells(3),3),:)        [vertex_4_x,vertex_4_y] = vertex(cell(node_belongs_to_cells(3),4),:)            endif        if(node_belongs_to_cells(2)!=0)        [vertex_1_x,vertex_1_y] = vertex(cell(node_belongs_to_cells(2),1),:)        [vertex_2_x,vertex_2_y] = vertex(cell(node_belongs_to_cells(2),2),:)        [vertex_3_x,vertex_3_y] = vertex(cell(node_belongs_to_cells(2),3),:)        [vertex_4_x,vertex_4_y] = vertex(cell(node_belongs_to_cells(2),4),:)            endif        if(node_belongs_to_cells(1)!=0)        %[vertex_1_x,vertex_1_y] = vertex(cell(node_belongs_to_cells(1),1),:)        %[vertex_2_x,vertex_2_y] = vertex(cell(node_belongs_to_cells(1),2),:)        %[vertex_3_x,vertex_3_y] = vertex(cell(node_belongs_to_cells(1),3),:)        %[vertex_4_x,vertex_4_y] = vertex(cell(node_belongs_to_cells(1),4),:)            endif        RHS = 0;endfunction