function RHS = rhs_integrate(SF,vertex,cell)%value of integrationval=0;first_shape_function = SF(1,:);%first shape function belongs to 1th nodenode_belongs_to_cells = node_to_cells(1,vertex,cell);%cellwise integration?if(node_belongs_to_cells(4)!=0)[vertex_1_x,vertex_1_y] = vertex(cell(node_belongs_to_cells(4),1),:)[vertex_2_x,vertex_2_y] = vertex(cell(node_belongs_to_cells(4),2),:)[vertex_3_x,vertex_3_y] = vertex(cell(node_belongs_to_cells(4),3),:)[vertex_4_x,vertex_4_y] = vertex(cell(node_belongs_to_cells(4),4),:)endifendfunction