function RHS = rhs_integrate(SF,vertex,cell)    RHS = zeros(length(vertex),1);    for i=1:length(vertex)        %value of integration        val=0;        i_th_shape_function = SF(i,:)        f = @(x,y) cos(x*pi)*cos(y*pi).*hf_eval_poly(x,y,i_th_shape_function) ;         %first shape function belongs to 1th node        node_belongs_to_cells = node_to_cells(i,vertex,cell);            %cellwise integration        if(node_belongs_to_cells(4)!=0)            vertex_1_x = vertex(cell(node_belongs_to_cells(4),1),1)            vertex_1_y = vertex(cell(node_belongs_to_cells(4),1),2)            vertex_2_x = vertex(cell(node_belongs_to_cells(4),2),1)            vertex_3_y = vertex(cell(node_belongs_to_cells(4),3),2)            val = val + dblquad(f,vertex_1_x,vertex_2_x,vertex_1_y,vertex_3_y)        endif            if(node_belongs_to_cells(3)!=0)            vertex_1_x = vertex(cell(node_belongs_to_cells(3),1),1)            vertex_1_y = vertex(cell(node_belongs_to_cells(3),1),2)            vertex_2_x = vertex(cell(node_belongs_to_cells(3),2),1)            vertex_3_y = vertex(cell(node_belongs_to_cells(3),3),2)            val = val + dblquad(f,vertex_1_x,vertex_2_x,vertex_1_y,vertex_3_y);        endif            if(node_belongs_to_cells(2)!=0)            vertex_1_x = vertex(cell(node_belongs_to_cells(2),1),1)            vertex_1_y = vertex(cell(node_belongs_to_cells(2),1),2)            vertex_2_x = vertex(cell(node_belongs_to_cells(2),2),1)            vertex_3_y = vertex(cell(node_belongs_to_cells(2),3),2)            val = val + dblquad(f,vertex_1_x,vertex_2_x,vertex_1_y,vertex_3_y)        endif            if(node_belongs_to_cells(1)!=0)            vertex_1_x = vertex(cell(node_belongs_to_cells(1),1),1)            vertex_1_y = vertex(cell(node_belongs_to_cells(1),1),2)            vertex_2_x = vertex(cell(node_belongs_to_cells(1),2),1)            vertex_3_y = vertex(cell(node_belongs_to_cells(1),3),2)            val = val + dblquad(f,vertex_1_x,vertex_2_x,vertex_1_y,vertex_3_y)        endif        RHS(i) = val;    endforendfunction