function [dx_SF,dy_SF] = sf_derivate(SF)        % Derive SF in x    dx_SF = zeros(length(SF),length(SF));    for row=1:length(SF)        %read i-th row        func = SF(row,:);        %dx_SF        dx_SF(row,1) = func(2);        polynomial_degree= sqrt(length(SF))-1;        for i=1:polynomial_degree            for j=1:polynomial_degree                dx_SF(row,hf_exponent_to_column(i-1,j))= i*func(hf_exponent_to_column(i,j));            endfor        endfor    endfor        % Derive SF in y    dy_SF = zeros(length(SF),length(SF));    for row=1:length(SF)        %read i-th row        func = SF(row,:);        %dx_SF        dy_SF(row,1) = func(2);        polynomial_degree= sqrt(length(SF))-1;        for i=1:polynomial_degree            for j=1:polynomial_degree                dy_SF(row,hf_exponent_to_column(i,j-1))= j*func(hf_exponent_to_column(i,j));            endfor        endfor    endforendfunction