function SF = sf_generate(polynomial_deg)    dimension_domain = 2;    k = 0;    % nodes_counter gives us the number of nodes we except on a unit square for a specific polynomial degree    nodes_counter =  (factorial(dimension_domain)/(factorial(k)*factorial(dimension_domain-k))) * polynomial_deg^k * (polynomial_deg+1)^(dimension_domain-k)    %row_counter gives us how many rows we have     row_counter = polynomial_deg + 1     nodes_per_row = nodes_counter / row_counter    %calculate mesh_size so we can generate a mesh     mesh_size = 1/(nodes_per_row-1)    %vertex matrix is here our nodes matrix    [vertex,cell] = mesh_generate(mesh_size);    for i=1:nodes_counter        % need a matrix containing data for node i. I-th Shape function on node i equals to one, on other nodes 0. Node i equals to the vertex i.        data_set= zeros(length(vertex),3);        for j=1:length(vertex)            data_set(j,:) = [vertex(j,1),vertex(j,2), hf_kronecker_delta(j,i) ];        endfor        %Interpolate to get the i-th shape function.        SF(i,:) = hf_interpolation(data_set);    endforendfunction