function SF = sf_generate(polynomial_deg)    dimension_domain = 2;    k = 0;    % nodes_counter gives us the number of nodes we except for a specific polynomial degree    nodes_counter =  (factorial(dimension_domain)/(factorial(k)*factorial(dimension_domain-k))) * polynomial_deg^k * (polynomial_deg+1)^(dimension_domain-k);    row_counter = polynomial_deg + 1 ;    nodes_per_row = nodes_counter / row_counter;    % calculate mesh_size so we can generate a mesh. We can use our mesh_generate because we want our shape functions on the    % reference cell, which is a unit square. Our nodes should be equidistant. mesh_generate perfectley does that.    mesh_size = 1/(nodes_per_row-1);    % our nodes matrix is equal to the vertex matrix in this case    [node,cell] = mesh_generate(mesh_size);    for i=1:nodes_counter        % need a matrix containing data for node i. I-th Shape function on node i equals to one, on other nodes 0.        data_set= zeros(length(node),3);        for j=1:length(node)            data_set(j,:) = [node(j,1),node(j,2), hf_kronecker_delta(j,i) ];        endfor        % interpolate to get the i-th shape function.        SF(i,:) = hf_interpolation(data_set);    endforendfunction