function SF = sf_generate(polynomial_deg)    dimension_domain = 2;    k = 0;    nodes_counter =  (factorial(dimension_domain)/(factorial(k)*factorial(dimension_domain-k))) * polynomial_deg^k * (polynomial_deg+1)^(dimension_domain-k)    column_counter = polynomial_deg + 1     nodes_per_column = nodes_counter / column_counter     mesh_size = 1/(nodes_per_column-1)    [vertex,cell] = mesh_generate(mesh_size);        for i=1:nodes_counter            % need a matrix with data set for node i. Shape function on node i equals to one, on other nodes 0. Node i equals to the vertex i.        data_set= zeros(length(vertex),3);        for j=1:length(vertex)            data_set(j,:) = [vertex(j,1),vertex(j,2), hf_kronecker_delta(j,i) ];        endfor                SF(i,:) = hf_interpolation(data_set);            endforendfunction