function SM  = sm_assemble_global(mesh_size,polynomial_deg)    cells_per_row= (1/mesh_size);    number_of_nodes = ((polynomial_deg * cells_per_row)+1)^2;    SM_local = sm_assemble_local(mesh_size);    size_SM_local = length(SM_local);    SM = zeros(number_of_nodes);    for i=1:number_of_nodes        for j=1:number_of_nodes            for k=1:number_of_cells                if(mesh_appropriate_renumbering(mesh_size,polynomial_deg,k,i) != 0 && mesh_appropriate_renumbering(mesh_size,polynomial_deg,k,j) !=0 )                    SM(i,j)+= SM_local(mesh_appropriate_renumbering(mesh_size,polynomial_deg,k,i),mesh_appropriate_renumbering(mesh_size,polynomial_deg,k,j);                endif            endfor        endfor    endforendfunction