function SM  = sm_assemble_global(mesh_size,polynomial_deg)    cells_per_row= (1/mesh_size);    number_of_cells = cells_per_row^2;    number_of_nodes = ((polynomial_deg * cells_per_row)+1)^2;    SF = sf_generate(polynomial_deg);    SM = zeros(number_of_nodes);    cell_matrix = mesh_cell(mesh_size,polynomial_deg);    mesh_renumber(cell_matrix,1,1);    SM_local = sm_assemble_local(SF,mesh_size);    size_SM_local = length(SM_local);    for i=1:number_of_nodes        for j=1:number_of_nodes            for k=1:number_of_cells                if(mesh_renumber(cell_matrix,k,i) != 0 && mesh_renumber(cell_matrix,k,j) !=0 )                    SM(i,j)= SM(i,j) + SM_local(mesh_renumber(cell_matrix,k,i),mesh_renumber(cell_matrix,k,j));                endif            endfor        endfor    endforendfunction