function SM  = sm_assemble_global(mesh_size,polynomial_deg)cells_per_row= (1/mesh_size);number_of_nodes = ((polynomial_deg * cells_per_row)+1)^2;SM_local = sm_assemble_local(mesh_size);size_SM_local = length(SM_local);SM = zeros(number_of_nodes);SM_row = 1;SM_column= 1;while SM_row <= number_of_nodes && SM_column <= number_of_nodes    for j=1:size_SM_local        for k=1:size_SM_local            SM(SM_row,SM_column)=SM(SM_row,SM_column) + SM_local(j,k)            SM_column++;        endfor            SM_row++;            SM_column = 1;    endfor    SM_column= endwhileendfunction