function SM_local = sm_assemble_local(SF,mesh_size)    [dx_SF,dy_SF]= sf_derivate(SF);    SM_local = zeros(length(SF),length(SF));    for i=1:length(SF)        i_th_shape_function = SF(i,:);        for j=1:length(SF)            j_th_shape_function = SF(j,:);            val=0;            f = @(x,y) mesh_size^2 .* hf_eval_poly(x,y,i_th_shape_function).*hf_eval_poly(x,y,j_th_shape_function) ;            h = @(x,y) hf_eval_poly(x,y,sf_derivate(i_th_shape_function)).*hf_eval_poly(x,y,sf_derivate(j_th_shape_function)) ;            val = dblquad(f,0,1,0,1) + dblquad(h,0,1,0,1);            SM_local(i,j) = val;    endforendforendfunction