function linear_solver_analytics()    addpath(genpath([pwd '/functions']))        MIN_MESH_SIZE = 100;    ls_error_fgm = zeros(MIN_MESH_SIZE,1);    ls_error_cg = zeros(MIN_MESH_SIZE,1);    ls_error_minres = zeros(MIN_MESH_SIZE,1);    runtime_gmres = zeros(MIN_MESH_SIZE,1);    runtime_cg = zeros(MIN_MESH_SIZE,1);    runtime_minres = zeros(MIN_MESH_SIZE,1);        for i=1:MIN_MESH_SIZE        % Calculate Stiffness Matrix        mesh_size= 1/i ;        pol_deg= 1;        % Initialize our mesh and our coefficient matrix for the shape functions        [Vertex,Cell]=mesh_generate(mesh_size);        SF=sf_generate(pol_deg);        % sm_assemble_local computes the local stiffness matrix. In our case,the local stiffness matrix looks the same for every cell.        SM_local=sm_assemble_local_vectorized(mesh_size,SF);        % sm_assemble_global will give the global stiffness matrix        A=sm_assemble_global(mesh_size,SF,SM_local);        % Initialize right hand side of our linear system        f = @(x,y) cos(x*pi).*cos(y*pi);        b=rhs_integration(Vertex,Cell,SF,f);            % FGMRES        tic        % Preconditioning Matrix: Incomplete Cholesky factorization with no fill-in        %M = ichol(SM);        x1= ls_fgmres(A,b,100,10^(-10),50,eye(columns(A)),b);        gmres_runtime=toc;            % MINRES        tic        x2= ls_minres(A,b,b,100,10^(-10));        minres_runtime=toc;            % CG Linear solver        tic        x3=ls_cg(A,b,b);        cg_runtime=toc;            % Error by linear solver        ls_error_fgm(i)=(A*x1-b)'*(A*x1-b);        runtime_gmres(i)=gmres_runtime;        ls_error_cg(i)=(A*x3-b)'*(A*x3-b);        runtime_cg(i)=cg_runtime;        ls_error_minres(i)=(A*x2-b)'*(A*x2-b);        runtime_minres(i)=minres_runtime;        disp("Done Step:"),disp(i) ;            endfor    runtime_minres    x=1:MIN_MESH_SIZE;        figure(1)    plot(x,ls_error_fgm,'b',x,ls_error_cg,'r',x,ls_error_minres,'g');        figure(2)    plot(x,runtime_gmres,'b',x,runtime_cg,'r',x,runtime_minres,'g');        end