function linear_solver_analytics()    % Add all subfolders to working directory    add_fem_lab();        % Define the smallest mesh size in the form of 1/mesh_size.     MIN_MESH_SIZE = 100;        % Initialize error vectors containing the errors for different mesh sizes for the different linear solvers    ls_error_gmres = zeros(MIN_MESH_SIZE,1);    ls_error_cg = zeros(MIN_MESH_SIZE,1);    ls_error_minres = zeros(MIN_MESH_SIZE,1);        % Initialize vectors containing the runtimes for different mesh sizes for the different linear solvers    runtime_gmres = zeros(MIN_MESH_SIZE,1);    runtime_cg = zeros(MIN_MESH_SIZE,1);    runtime_minres = zeros(MIN_MESH_SIZE,1);            % Loop from 1 until 1/MIN_MESH_SIZE for even mesh_size numbers and solve FEM problem for each mesh size and    % calculate the error and the runtime for the different linear solvers.     for i=1:MIN_MESH_SIZE        i=i+1;                % Calculate Stiffness Matrix        mesh_size= 1/i ;        pol_deg= 1;        % Initialize our mesh and our coefficient matrix for the shape functions        [Vertex,Cell]=mesh_generate(mesh_size);        SF=sf_generate(pol_deg);        % sm_assemble_local computes the local stiffness matrix. In our case,the local stiffness matrix looks the same for every cell.        SM_local=sm_assemble_local_vectorized(mesh_size,SF);        % sm_assemble_global will give the global stiffness matrix        A=sm_assemble_global(mesh_size,SF,SM_local);        % Initialize right hand side of our linear system        f = @(x,y) cos(x*pi).*cos(y*pi);        b=rhs_integration_new(Vertex,Cell,SF,f);            % GMRES        tic        x1= gmres(A,b) ;        gmres_runtime=toc;            % MINRES        tic        x2= ls_minres(A,b,b,100,10^(-10));        minres_runtime=toc;            % CG Linear solver        tic        x3=ls_cg(A,b,b);        cg_runtime=toc;            % Error and runtime for GMRES        ls_error_gmres(i)=(A*x1-b)'*(A*x1-b);        runtime_gmres(i)=gmres_runtime;                % Error and runtime for CG        ls_error_cg(i)=(A*x3-b)'*(A*x3-b);        runtime_cg(i)=cg_runtime;                % Error and runtime for MINRES        ls_error_minres(i)=(A*x2-b)'*(A*x2-b);        runtime_minres(i)=minres_runtime;                disp("Done Step:"),disp(i) ;        fflush(stdout);            endfor        x=1:MIN_MESH_SIZE;    % Plot error results    figure(1)    plot(x,ls_error_gmres,'b',x,ls_error_cg,'r',x,ls_error_minres,'g');    axis([1 MIN_MESH_SIZE]);    xlabel("1/MESH SIZE");    ylabel("L2 Error");        % Plot runtime results    figure(2)    plot(x,runtime_gmres,'b',x,runtime_cg,'r',x,runtime_minres,'g');    axis([1 MIN_MESH_SIZE]);    xlabel("1/MESH SIZE");    ylabel("L2 Error");       end